package com.test.ch03;

public class EX06NotOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		boolean result = true;
		System.out.printf("result = %b%n", result);
		System.out.printf("!result = %b%n", !result);
		System.out.printf("!!result = %b%n", !!result);
		System.out.printf("!!!result = %b%n", !!!result);

	}

}
