package com.test.ch03;

public class Ex02UnaryOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 5;
		int b = 3;
		System.out.printf("%d + %d = %d%n", a, b, a+b );
		
		b = -b;
		System.out.printf("%d + %d = %d%n", a, b, a+b);
		
		b = -b;
		System.out.printf("%d + %d = %d%n", a, b, a+b);
				

	}

}
